import React from 'react';
import { Image } from 'react-bootstrap';
import { new1 } from './public/images/p1.jpg';
import './index.css';

class AddProduct extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      Pname: '',
      PImage: '',
      PDescription: '',
      PQuantity: '',
      Pprice: '',
    };
  }

  render() {
    return (
      <>
        <h1>Create page</h1>
        <div>
          <form>
            <label className='porduct-detail'>
              Product Name:
              <input type='text' name='name' />
            </label>
            <label className='porduct-detail'>
              Product Iamge:
              <img src='' alt='imageval' />
            </label>
            <label className='porduct-detail'>
              Product Description:
              <textarea />
            </label>
            <label className='porduct-detail'>
              Product Quantity:
              <input type='text' name='Quantity' />
            </label>
            <label className='porduct-detail'>
              Product price:
              <input type='text' name='price' />
            </label>
            <input type='submit' value='Submit' />
          </form>
          <Image
            src={new1}
            alt={'imag'}
            className={'newimage'}
            id={'image'}
            dpr='auto'
            gravity='face'
            crop='thumb'
            width='100'
            height='100'
            zoom='0.8'
            sizes='100vw'
            fetchFormat='auto'
            quality='auto'
            type='fetch'
            secure
            responsive
            animationClassName='user-image'
          />
        </div>
      </>
    );
  }
}

export default AddProduct;
