import React from 'react';
import axios from 'axios'
import  new1  from '../images/p1.jpg';
import '../index.css';

class AddProduct extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      Pname: '',
      PImage: '',
      PDescription: '',
      PQuantity: '',
      Pprice: '',
    };
  }

  submitdata = () => {
    const data = {name : this.state.Pname}
    axios.post('http://localhost:5000/api/products',{data}).then((response) => {
      console.log("pp",response?.data)
          });
  }
  render() {
    return (
      <>
        <h1>Create page</h1>
        <div>
          <form>
            <label className='porduct-detail'>
              Product Name:
              <input type='text' name='name' />
            </label>
            <label className='porduct-detail'>
              Product Iamge:
              <img src= {new1} alt='imageval' />
            </label>
            <label className='porduct-detail'>
              Product Description:
              <textarea />
            </label>
            <label className='porduct-detail'>
              Product Quantity:
              <input type='text' name='Quantity' />
            </label>
            <label className='porduct-detail'>
              Product price:
              <input type='text' name='price' />
            </label>
            <input type='submit' value='Submit' onClick={() => this.submitdata} />
          </form>
          
        </div>
      </>
    );
  }
}

export default AddProduct;
